void waveform_get(int wf, Waveform *waveform) {
    switch(wf) {
    case SINE:
        waveform->waveform = waveform_sine;
        waveform->message = message_sine;
        waveform->delay = 999;
        break;
    case TRIANGLE:
        waveform->waveform = waveform_triangle;
        waveform->message = message_triangle;
        waveform->delay = 499;
        break;
    case SAWTOOTH:
        waveform->waveform = waveform_sawtooth;
        waveform->message = message_sawtooth;
        waveform->delay = 249;
        break;
    case AM:
        waveform->waveform = waveform_am;
        waveform->message = message_am;
        waveform->delay = 124;
        break;
    default:
        waveform->waveform = waveform_none;
        waveform->message = message_none;
        waveform->delay = 0xFFFF;
        break;
    }
}